interface TemplateSelectionProps {
  onSelect: (template: string) => void;
  addShape?: (shape: any) => void;
  addConnection?: (connection: any) => void;
}

const TemplateSelection: React.FC<TemplateSelectionProps> = ({
  onSelect,
  addShape,
  addConnection,
}) => {
  // Function to apply template
  const applyTemplate = (templateKey: keyof typeof TEMPLATES) => {
    const template = TEMPLATES[templateKey];

    // Only apply template if addShape and addConnection are available
    if (addShape && addConnection) {
      // Array to store shape IDs
      const shapeIds: string[] = [];

      // Create all shapes
      template.shapes.forEach((shapeData) => {
        const id = `${shapeData.type}-${Date.now()}-${Math.random()
          .toString(36)
          .substr(2, 5)}`;

        addShape({
          ...shapeData,
          id,
          preview: <div>Preview</div>,
        });

        shapeIds.push(id);
      });

      // Create all connections
      template.connections.forEach((connData) => {
        const fromId = shapeIds[parseInt(connData.from)];
        const toId = shapeIds[parseInt(connData.to)];

        addConnection({
          id: `conn-${Date.now()}-${Math.random().toString(36).substr(2, 5)}`,
          from: fromId,
          to: toId,
          points: [],
          style: connData.style,
          fromPoint: connData.fromPoint,
          toPoint: connData.toPoint,
        } as any);
      });
    }

    // Always call onSelect
    onSelect(templateKey);
  };

  return (
    <div className="min-h-screen bg-white flex items-center justify-center p-4">
      <div className="max-w-5xl w-full">
        <div className="text-center mb-12">
          <h1 className="text-3xl font-semibold text-gray-900 mb-4">
            Begin Your Design
          </h1>
          <p className="text-gray-500">
            Choose how you want to start your diagram
          </p>
        </div>
        <div className="grid md:grid-cols-2 gap-8">
          {/* GSN Template */}
          <div
            className="bg-white rounded-xl border border-gray-200 p-6 hover:border-blue-500 cursor-pointer transition-all hover:shadow-lg"
            onClick={() => applyTemplate("gsn")}
          >
            <div className="h-64 bg-gray-50 rounded-lg mb-4 flex items-center justify-center">
              <div
                className="w-full h-full p-2"
                dangerouslySetInnerHTML={{ __html: TEMPLATES.gsn.previewSvg }}
              />
            </div>
            <h2 className="text-xl font-medium text-gray-900 mb-2">
              GSN Template
            </h2>
            <p className="text-sm text-gray-500">
              Start with a pre-designed Goal Structuring Notation template.
            </p>
          </div>

          {/* SACM Template */}
          <div
            className="bg-white rounded-xl border border-gray-200 p-6 hover:border-blue-500 cursor-pointer transition-all hover:shadow-lg"
            onClick={() => applyTemplate("sacm")}
          >
            <div className="h-64 bg-gray-50 rounded-lg mb-4 flex items-center justify-center">
              <div
                className="w-full h-full p-2"
                dangerouslySetInnerHTML={{ __html: TEMPLATES.sacm.previewSvg }}
              />
            </div>
            <h2 className="text-xl font-medium text-gray-900 mb-2">
              SACM Template
            </h2>
            <p className="text-sm text-gray-500">
              Begin with a Structured Assurance Case Meta-model template.
            </p>
          </div>
        </div>
      </div>
    </div>
  );
};

export default TemplateSelection;
